import React from 'react';
import { Box, Grid, Paper, Stack, Typography } from '@mui/material';
import "./styles.css";
import { Link } from 'react-router-dom';

const canvases = [ 'Stickies', 'Notepad', 'Blank canvas'];

const HomePage = ()  => {
  return (
    // center everything to middle of page 
    <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <Grid container justifyContent="center" alignItems="center" textAlign="center"
      >
        <Grid item xs={12}>
          <Typography variant="h2">
              Welcome to Jot
            </Typography>
            <Typography variant="subtitle1">
              Pick your canvas
            </Typography>
            <Grid item container columnSpacing={3} sx={{ justifyContent: 'center' }}>
              {canvases.map(( item, index ) => (
                <Grid key={index} item xs={3}>
                  <Box component={Paper} sx={{ textAlign: 'center', py: 12 }}>
                    <Typography component={Link} to={`/${item.toLowerCase().split(' ').join('')}`}>{item}</Typography>
                  </Box>
                </Grid>
              ))}
              </Grid>
        </Grid>
      </Grid>
    </Box>
  );
};

export default HomePage;
